import React, { useState } from "react";
import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import ExpenseFilter from "./ExpenseFilter";
import Card from "../UI/Card";

function Expenses(props) {

    const [filteredYear, setFilteredYear] = useState();

    const yearFilterHandler = selectedYear => {
        setFilteredYear(selectedYear);
    };

    return(
        <Card className="expenses">
            <ExpenseFilter onYearChange={yearFilterHandler}/>
            <ExpenseItem title={
                    props.items[0].title
                }
                amount={
                    props.items[0].amount
                }
                date={
                    props.items[0].date
                }/>
            <ExpenseItem title={
                    props.items[1].title
                }
                amount={
                    props.items[1].amount
                }
                date={
                    props.items[1].date
                }/>
            <ExpenseItem title={
                    props.items[2].title
                }
                amount={
                    props.items[2].amount
                }
                date={
                    props.items[2].date
                }/>
            <ExpenseItem title={
                    props.items[3].title
                }
                amount={
                    props.items[3].amount
                }
                date={
                    props.items[3].date
                }/>
        </Card >);
}

export default Expenses;
